import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import Quiz from './Quiz';
import categories from '../databases/categories.json'

function QuizSelect() {
    const navigate = useNavigate();

    return (
        <main className='page-main d-flex justify-content-center align-items-center'>
            <div className='text-center'>Quiz Select</div>
            <div className='home-buttons'>
                {categories.map(category => {
                    return(
                        <button key={category.id} className='btn' onClick={() => {navigate('/quiz', {state:{id: category.id, categoryId: category.categoryId}})}}>{category.name}</button>
                    )
                })}
            </div>
        </main>
    )
}

export default QuizSelect
