import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import Quiz from './Quiz';

function QuizSelect() {
    const navigate = useNavigate();

    return (
        <main className='page-main d-flex justify-content-center align-items-center'>
            <div className='text-center'>Quiz Select</div>
            <div className='home-buttons'>
                <button className='btn' onClick={() => {navigate('/quiz', {state:{id:1,name:'sabaoon'}})}}>General</button>
                <button className='btn' onClick={() => {Quiz('entertainment')}}>Entertainment</button>
                <button className='btn' onClick={() => {Quiz('science')}}>Science</button>
                <button className='btn' onClick={() => {Quiz('mythology')}}>Mythology</button>
                <button className='btn' onClick={() => {Quiz('sports')}}>Sports</button>
                <button className='btn' onClick={() => {Quiz('history')}}>History</button>
                <button className='btn' onClick={() => {Quiz('politics')}}>Politics</button>
                <button className='btn' onClick={() => {Quiz('art')}}>Art</button>
                <button className='btn' onClick={() => { navigate('/') }}>Home</button>
            </div>
        </main>
    )
}

export default QuizSelect
