import React from "react";

const API = async (categoryId) => {
    const r = await fetch(`https://opentdb.com/api.php?amount=10&category=${categoryId}&difficulty=easy&type=multiple`)

    const response = await r.json();
    console.log(response);
    const questionList = response.results
    console.log(questionList);
    return questionList;
}
export default API
