import React, { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const API = props => {
    const navigate = useNavigate();
    const location = useLocation();
    const categoryId = location.state.categoryId;
    console.log(categoryId);

    const getQuestions = async(categoryId) => {
        console.log(categoryId);
        const r = await fetch(`https://opentdb.com/api.php?amount=10&category=${categoryId}&difficulty=easy&type=multiple`)

        const response = await r.json();
        console.log(response);
        const questionList = response.results
        console.log(questionList);
        return questionList;
    }

    const questions = getQuestions(categoryId);
    navigate('/quiz', {questions})

    return(
        <div>api</div>
    )


}
export default API
